mapper.select:
select mp.available,mp.state,mp.id,mp.num ,m.name as machineName,m.`code` as machineCode,u.name as merchantName,c.`name` as commodityName,cs.price,mp.commodity_id as commodityId
from machine_position mp
left join machine m on mp.machine_id = m.id
left join merchant me on me.id = m.id
left join commodity c on c.id = mp.commodity_id
left join sys_user u on  u.id = me.user_id
left join commodity_stock cs on cs.commodity_id = mp.commodity_id
where mp.flag = false

    and m.id = #{machineId}


    and m.code = #{machineCode}


    and m.name = #{machineName}

limit #{startPosition},#{pageSize}

select count(mp.id) from machine_position mp left join machine m on mp.machine_id = m.id left join merchant me on me.id = m.id left join commodity c on c.id = mp.commodity_id left join sys_user u on u.id = me.user_id left join commodity_stock cs on cs.commodity_id = mp.commodity_id where mp.flag = false
and m.id = #{machineId}
and m.code = #{machineCode}
and m.name = #{machineName}
